mapper.insert:
INSERT INTO category_relationship (
  ancestor_idx
  ,descendant_idx
)VALUES(
  #{descendant_idx}
  ,#{descendant_idx}
);

INSERT INTO category_relationship (
  ancestor_idx
  ,descendant_idx
)
SELECT 
  ancestor_idx
  ,#{descendant_idx}
FROM category_relationship
WHERE descendant_idx = #{ancestor_idx}
AND descendant_idx != #{descendant_idx}
